void FFT(Poly &a,int n){
	com x,y,w,w_n;
	for(int i=(n>>1);i;i>>=1){
		w_n={cos(pi/i),sin(pi/i)};
		for(int j=0;j<n;j+=(i<<1)){
			w={1.0,0.0};
			for(int k=j;k<i+j;k+=1){
				x=a[k]; y=a[k+i];
				a[k]=x+y; a[k+i]=(x-y)*w;
				w=w*w_n;
			}
		}
	}
	return;
}
void IFFT(Poly &a,int n){
	com x,y,w,w_n;
