#include<bits/stdc++.h>
using namespace std;
struct haha{
    string x;
    string y;
};//用于记录每天的幸存者名单
int main(){
    int n;
    haha x[1200];
    string a,b,s;
    cin>>a>>b; 
    x[0].x=a;
    x[0].y=b;//记录初始二位的名字
    cin>>n;
    for(int i=1;i<=n;i++){
    	cin>>a>>b;
    	if(a==x[i-1].x){
    		x[i].x=b;
    		x[i].y=x[i-1].y;
        }
    	if(a==x[i-1].y){
    		x[i].x=x[i-1].x;
    		x[i].y=b;
        }//哪位仁兄被移除就将替补放在这位的位置上
    }
    for(int i=0;i<=n;i++){
        cout<<x[i].x<<" "<<x[i].y<<endl; 
    }//包括最初二位全部输出
    return 0;
} 
```

