#include<bits/stdc++.h>
using namespace std;
int n,x;
int a[3]={0,1,2};//初始数组
int main(){
	cin>>n>>x;
	n%=6;//六个循环剪掉
	for(int i=1;i<=n;++i){
		if(i%2)swap(a[1],a[0]);//如果是第奇数次操作交换中间和左边
		else swap(a[1],a[2]);//偶数次交换中间和右边
	}
	cout<<a[x]<<endl; //输出
return 0;
}

