{
	ios::sync_with_stdio(false);cin.tie(0);
	cin >> n >> m >> k;
	if(n&1 && k<=m)/*n为奇数且k≤m时 才有必胜态的存在可能*/
	{
		for(RI i=1,lim=(int)sqrt(m)+1;i<=lim/*也可以写成i*i<=m 但速度稍慢*/;++i)
			if(!(m%i) && ((i>=k && m/i>1) || (m/i>=k && i>1)))
			{
				/*对于m存在因子i满足i≠1且m/i≥k或i≥k 则Timur可以第一轮就将状态改变为偶数条木棍 即必败态*/
				puts("Timur");
				return 0;
			}
		puts("Marsel");	
	}
	else puts("Marsel");
	return 0;
}
