```
#include<iostream>
#include<algorithm>
#include<stdio.h>
#include<cstdio>

using namespace std;
bool k[105][105]={false};
int n,m;
void dg(int m)
{
    if(m==0)//终止条件
        return;
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<n;j++)
        {
            if(!k[i][j])
            {
                if(i==j)//因为我是从头循环的，所以能加在对角线上就加在对角线上
                {
                    k[i][j]=true;
                    m--;
                    dg(m);//递归下一层
                    return;//返回上一层
                }
                if(m>1&&i!=j)//如果不属于对角线m又大于1就把它和它相对的那个赋为true
                {
                    k[i][j]=k[j][i]=true;
                    m-=2;
                    dg(m);
                    return;
                }
            }
        }
    }
}
int main()
{
    cin>>n>>m;
    if(n*n<m)//如果格子数不够就puts("-1")
    {
        puts("-1");
        return 0;
    }
    else if(n*n==m)//等于就全输出1
    {
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
                cout<<"1"<<" ";
            puts("");
        }
        return 0;
    }
    dg(m);//开始递归
    for(int i=0;i<n;i++)//输出
    {
        for(int j=0;j<n;j++)
        {
            if(k[i][j])
                cout<<"1"<<" ";
            else
                cout<<"0"<<" ";
        }
        puts("");
    }
    return 0;//AC
}
