bool vis[MAXX];
int main( )
{
	scanf("%d%d",&n,&w);
	for(i=1;i<=n;i++)
	scanf("%d",&a[i]);
	for(i=1;i<=n;i++)
	{
		if(a[i]%2==0) d[i]=a[i]/2;
		else d[i]=a[i]/2+1;
		for(j=1;j<=n;j++)
		if(a[i]>a[j]&&d[j]>d[i])
		d[i]=d[j];
	}                 //每个杯子最少能装多少酒
	for(i=1;i<=n;i++)
	ans+=d[i];
	if(ans>w)         //如果多了 输出
	printf("-1");
	if(ans==w)        //如果刚刚好 输出
	for(i=1;i<=n;i++)
	printf("%d ",d[i]);
	if(ans<w)         //如果少了 开始把多出来的酒倒出来
	{
		ans=w-ans;
		while(ans>0)
		{
			maxx=-1;
			for(i=1;i<=n;i++)      //每次找最大的值
			if(maxx<a[i]&&vis[i]==0)
			maxx=a[i],t=i;
			ans=ans-(a[t]-d[t]);   //然后倒酒 不能超出酒杯容量
			d[t]=a[t];vis[t]=1;
		}                          //我感觉之前的题解是这么错的————没有考虑酒杯容量把剩下的酒全装在一个杯子
		if(ans<0)
		d[t]=d[t]+ans;
		for(i=1;i<=n;i++)
		printf("%d ",d[i]);        //输出
	}
}
