#include<bits/stdc++.h>    //万能的脑袋
#pragma GCC optimize(3)   //吸臭氧（虽然根本没必要）
using namespace std;
int s,va,vb,ta,tb;       //定义
int main()
{
    cin>>s>>b1>>b2>>a1>>a2;  //读入
    if((a1+a1+b1*s)<(a2+a2+b2*s))     //1快
    {
        cout<<"First";
    }
    else if((a1+a1+b1*s)>(a2+a2+b2*s))//2快
    {
        cout<<"Second";
    }
    else              //一样
    {
        cout<<"Friendship";
    }
    return 0;
}
```


