```cpp
#include<iostream>
#include<cstdio>
#include<cmath>
#include<cstring>
using namespace std;
struct node{
	int a;
	int b;
	int c;
	int d;
	int e;
}point[1100];//一个结构体数组
bool judge[1100];
int n;
int main()
{
	scanf("%d", &n);
	if (n > 11){
		cout << 0 << endl;
		return 0;
	}
	for (int i=1; i<=n; i++)
	{
		scanf("%d", &point[i].a);
		scanf("%d", &point[i].b);
		scanf("%d", &point[i].c);
		scanf("%d", &point[i].d);
		scanf("%d", &point[i].e);
	}
	memset (judge, 0, sizeof (judge)); 
	for (int i=1; i<=n; i++)
	{
		for (int j=1; j<=n; j++)
		{
			if (j == i) continue;
			for (int k=1; k<=n; k++)
			{
				if (k == i || k == j) continue;//三位循环枚举每维的数据
				node x, y;
				x.a = point[j].a - point[i].a;
				y.a = point[k].a - point[i].a;
				x.b = point[j].b - point[i].b;
				y.b = point[k].b - point[i].b;
				x.c = point[j].c - point[i].c;
				y.c = point[k].c - point[i].c;
				x.d = point[j].d - point[i].d;
				y.d = point[k].d - point[i].d;
				x.e = point[j].e - point[i].e;
			    y.e = point[k].e - point[i].e;
			    //按题目要求处理
			    if (x.a * y.a + x.b * y.b + x.c * y.c + x.d * y.d + x.e * y.e > 0) {
				   judge[i] = 1;
				   break;
				}
			}
		}
	}
	int sum = 0;
	for (int i=1; i<=n; i++)
	{
		if (judge[i] == 0) sum ++;
	}
	cout << sum << endl;
	for (int i=1; i<=n; i++)
	{
		if (judge[i] == 0) cout << i << " ";/。判好坏
	}
	return 0;
}
```
