static constexpr const int INF(INT_MAX);
static constexpr const int Mod(1e9 + 7);

namespace IO
{
    static inline int const Read()
    {
        int Ans(0);
        char Ch = getchar() , Las = ' ';

        while(!isdigit(Ch))
        {
            if(Ch == '-')
                Las = Ch;

            Ch = getchar();
