    static inline bool const Cmp(int a , int b)
    {
        return Ans[a] < Ans[b];
    }

    static inline void const DFS(int _a , int b , int c)
    {
        if(!_a)
            return;

        Ans[_a--] = a[b] , c-= a[b];

        if(Dp[_a][b][c])
            return DFS(_a , b , c);

        return DFS(_a , b - 1 , c);
