{
	scanf("%lld%lld",&n,&k);
	for(int i = 1;i <= n;i++)
		scanf("%lld",&t[i]);
	for(int i = 1;i <= n;i++)
	{
		scanf("%lld",&a[i]);
		if(t[i] == 2)
			a[i] = -a[i];
		pre[i + 1] = pre[i] + a[i];
	}
	for(int i = 1;i <= n + 1;i++)
	{
		if(!mp[pre[i]])
			mp[pre[i]] = ++mpcnt;
		if(!mp[pre[i] + k])
			mp[pre[i] + k] = ++mpcnt;
		if(!mp[pre[i] - k])
			mp[pre[i] - k] = ++mpcnt;
		leftcp[i] = mp[pre[i] + k];
		rightcp[i] = mp[pre[i] - k];
		pre[i] = mp[pre[i]];
	}
	scanf("%lld",&m);
	for(int i = 1;i <= m;i++)
		scanf("%lld%lld",&q[(q[i].id = i)].l,&q[i].r),q[i].r++;
	init();
	std::sort(q + 1,q + 1 + m,cmp);
	int l = 1,r = 0,now = 0;
	for(int i = 1;i <= m;i++)
	{
		int ql = q[i].l,qr = q[i].r;
		while(ql < l)
		{
			l--;
			now += cnt[leftcp[l]];
			cnt[pre[l]]++;
		}
		while(r < qr)
		{
			r++;
			now += cnt[rightcp[r]];
			cnt[pre[r]]++;
		}
		while(l < ql)
		{
			cnt[pre[l]]--;
			now -= cnt[leftcp[l]];
			l++;
		}//
		while(qr < r)
		{
			cnt[pre[r]]--;
			now -= cnt[rightcp[r]];
			r--;
		}
		ans[q[i].id] = now;
	}
	for(int i = 1;i <= m;i++)
		printf("%lld\n",ans[i]);
	return 0;
}
