void solve(){
	string s,t;
	cin>>s>>t;
	if(s=="typeof"){
		int x,y;
		x=y=0;
		s=t;
		while(s.size()&&s[0]=='&')s=s.substr(1,s.size()-1),x++;
		while(s.size()&&s[s.size()-1]=='*')s=s.substr(0,s.size()-1),y++;
		if(mp.find(s)==mp.end())over("errtype")
		x=y-x;
