		if(st=="errtype"){
			cout<<"errtype"<<endl;
			return;
		}
		int z=0;
		for(int i=st.size()-1;i>=0&&st[i]=='*';i--)z++,st=st.substr(0,st.size()-1);
		if(z+x<0)cout<<"errtype\n";
		else{
