		while(s.size()&&s[s.size()-1]=='*')s=s.substr(0,s.size()-1),y++;
		x=y-x;
		y=0;
		if(s=="errtype"){
			mp[t]="errtype";
			return;
		}
		if(mp.find(s)==mp.end()){
			mp[s]=mp[t]="errtype";
			return;
		}
		string st=mp[s];
		if(st=="errtype"){
			mp[t]="errtype";
			return ;
		}
		int z=0;
		for(int i=st.size()-1;i>=0&&st[i]=='*';i--)z++,st=st.substr(0,st.size()-1);
		if(z+x<0)mp[t]="errtype";
		else{
			for(int i=0;i<z+x;i++)st+='*';
			mp[t]=st;
		}
	}
}
