    int num=1,a=-1;
    string s;
    cin>>s;
    for(int i=s.size()-1;i>=0;i--){
        if(s[i]=='0'){
            if(num==6){
            	i--;
            	a=1;
            	int k=0;
                while(i>=0){
                	if(s[i]=='1') k=1;
                	i--;
				}
				if(k) cout<<"yes";
				else cout<<"no";
            }
            else{
                num++;
            }
        }
    }
