			f[l][r][2] += cal(l, i - 1, 1) * cal
			(i, r, 1) % mod;
			f[l][r][2] %= mod;
		}
		return f[l][r][2];
	}
	for(int i = l + 1;i <= r;i++){
		if(!a[l][i]) continue;
		f[l][r][1] += (cal(l, i - 1, 1) + cal(l, i, 2)) * cal(i, r, 1) % mod;
		f[l][r][1] %= mod;
