```cpp
#include<iostream>
#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<cmath>
#include<algorithm>
#include<set>
#include<map>
#include<vector>
#include<queue>
using namespace std;
#define MAX 10000000
#define ll long long
inline int read()
{
    int x=0,t=1;char ch=getchar();
    while((ch<'0'||ch>'9')&&ch!='-')ch=getchar();
    if(ch=='-')t=-1,ch=getchar();
    while(ch<='9'&&ch>='0')x=x*10+ch-48,ch=getchar();
    return x*t;
}
bool zs[MAX+1];
int pri[MAX+1],tot,mu[MAX+1],smu[MAX+1];
void pre()
{
	zs[1]=true;mu[1]=1;
	for(int i=2;i<=MAX;++i)
	{
		if(!zs[i])pri[++tot]=i,mu[i]=-1;
		for(int j=1;j<=tot&&i*pri[j]<=MAX;++j)
		{
			zs[i*pri[j]]=true;
			if(i%pri[j])mu[i*pri[j]]=-mu[i];
			else break;
		}
	}
	for(int i=1;i<=MAX;++i)smu[i]=smu[i-1]+mu[i];
}
int Count(int p,int n)
{
	int ret=0;
	for(int i=1;i*i<=p;++i)
	{
		if(p%i)continue;
		ret+=mu[i]*(n/i);
		if(p/i!=i)ret+=mu[p/i]*(n/(p/i));
	}
	return ret;
}
int main()
{
	int T=read();pre();
	while(T--)
	{
		int x=read(),p=read(),K=read();
		int ss=Count(p,x);
		int l=x+1,r=1e7,ans;
		while(l<=r)
		{
			int mid=(l+r)>>1;
			int sss=Count(p,mid);
			if(sss-ss>=K)ans=mid,r=mid-1;
			else l=mid+1;	
		}
		printf("%d\n",ans);
	}
	return 0;
}

```

