  m:ansistring;
  a:array[0..2000000] of char;
  i,len,k,ans:longint;
begin
  readln(m); len:=length(m);
  for i:=1 to len do
  begin
    a[i]:=m[i];
    if a[i]='1' then inc(k);
  end;//字符串转数组储存，更加快一点
  while true do
  begin
    while a[len]='0' do
    begin
      dec(len);
      inc(ans);
    end;//去掉末尾的0
    if k=1 then break;
    while a[len]='1' do
    begin
      dec(len);
      dec(k);
      inc(ans);
    end;//消除1
    inc(ans);
    a[len]:='1';
    inc(k);//最后还会产生1
    if k=1 then break;
  end;
  writeln(ans);
end.
