bool p(int i,int j)
{
	return(a[i+1][j]=='S'||a[i-1][j]=='S'||a[i][j+1]=='S'||a[i][j-1]=='S');
}
void work(int i,int j)
{
	if(a[i+1][j]=='.')
		a[i+1][j]='D';
	if(a[i-1][j]=='.')
		a[i-1][j]='D';
	if(a[i][j+1]=='.')
		a[i][j+1]='D';
	if(a[i][j-1]=='.')
		a[i][j-1]='D';
}
int main()
{
	cin>>r>>c;
	for(int i=1;i<=r;i++)
		for(int j=1;j<=c;j++)
			cin>>a[i][j];
	for(int i=1;i<=r;i++)
		for(int j=1;j<=c;j++)
			if(a[i][j]=='W')
			{
				if(p(i,j))
					return!(cout<<"No\n");
				work(i,j);
			}
	cout<<"Yes\n"; 
	for(int i=1;i<=r;i++)
	{
		for(int j=1;j<=c;j++)
			cout<<a[i][j];
