int main() {
	LL l, sum = 0, x = 0, flag = 1; // sum存储结果，flag存储正负
	cin >> s;
	l = strlen(s);
	F1 (i, 0, l - 1) { // 遍历字符串
		if (s[i] < '0' || s[i] > '9') { // 如果当前字符为运算符
			sum += x * flag; // 将运算符前面的数字加上
			x = 0; // 为计算下一个数字，x要清零
			if (s[i] == '+') flag = 1;
			if (s[i] == '-') flag = -1;
			x = x * 10 + (s[i] - '0'); // 将当前运算符当做数字计算进去
		}
		if (s[i] >= '0' && s[i] <='9') { // 把每一位数字转化为一个数字
			x *= 10;
			x += (s[i] - '0');
		}
	}
	sum += x * flag; // 加上最后一个数字
	printf("%lld\n", sum);
	return 0;
}

