        ++i;
    }
	while(i<=n)
	{
		if(str[i]=='+')
		{
			int ws=0,num=0;
			++i;
			while(str[i]>='0'&&str[i]<='9'&&i<=n)
			{
				++ws;//存储每个符号之后的数字位数 
				num=num*10+str[i]-'0';//存储每个符号之后的数字的值 
				++i;
			}
			ans=ans-5*std::pow(10,ws)+num;
		}
		else if(str[i]=='-')
		{
			int ws=0,num=0;
			++i;
			while(str[i]>='0'&&str[i]<='9'&&i<=n)
			{
				++ws;//存储每个符号之后的数字位数 
				num=num*10+str[i]-'0';//存储每个符号之后的数字的值 
				++i;
			}
			ans=ans+3*std::pow(10,ws)-num;
		}
	}
	printf("%d",ans);
	return 0;
