#include<bits/stdc++.h>//懒人万能库
using namespace std;
int n;//长度
string s;//字符串
bool flag;//判断变量
int main()
{
	scanf("%d",&n);//喜闻乐见的读入
	cin>>s;
	for(int i=1;i<n-1;i++)//循环开始，首尾各空一格
	{
		if(s[i]!='?'&&(s[i]==s[i-1]||s[i]==s[i+1]))//如果出现连续相同元素就输出
		{
			printf("NO");
			return 0;
		}
		if(s[i]=='?'&&(s[i+1]=='?'||s[i-1]=='?'||s[i-1]==s[i+1]))//判断2,3，情况
			flag=1;
	}
	if(flag||(!flag&&(s[0]=='?'||s[n-1]=='?')))//特判？在字符串首尾的情况
		printf("YES");
	else
		printf("NO");
    return 0;//程序拜拜
}
```
