贴上代码。
```
#include<bits/stdc++.h>
#define ll long long
#define rg register
using namespace std;
const int N=2e5+10;
inline ll read() {
	register ll s=0,f=0;
	register char ch=getchar();
	while(!isdigit(ch)) f|=(ch=='-'),ch=getchar();
	while(isdigit(ch)) s=(s<<1)+(s<<3)+(ch^48),ch=getchar();
	return f?-s:s;
}
int n,a[N>>1],b[N>>1],cnt,flag,m,t;
int pri[2*N];
vector<int>h[N];
bool vis[N*11],vp[2*N];
inline bool prime(int x){
	int k=x;
	for(int i=1;i<=m;i++){
		if(!(k%pri[i])&&vp[i])return 0;
		if(pri[i]>k)break;
	}
	return 1;
}
int main() {
	n=read();
	for(ll i=2; i<=N*10; i++) {//筛质数 
		if(!vis[i])pri[++m]=i;
		for(ll j=1; j<=m&&i*pri[j]<=N*10; j++) {
			vis[i*pri[j]]=1;
			if(!(i%pri[j]))break;
		}
	}
	for(int i=1; i<=m; i++) {//存每个数可以被哪些质数整除 
		for(int j=pri[i]; j<N; j+=pri[i]) {
			int x=j;
			while(!(x%pri[i])) {
				h[j].push_back(i);
				x/=pri[i];
			}
		}
	}
	for(int i=1; i<=n; i++) {
		a[i]=read();
		t=a[i];
		if(flag)break;//如果之前存在过一次b>a,后面就可以一直放质数 
		else {
			for(int j=0; j<h[t].size(); j++) {//检验是否与之前的b互质 
				if(vp[h[t][j]]) {
					flag=1;
					break;
				}
			}
			if(!flag) {//互质则放入 
				for(int j=0; j<h[t].size(); j++)vp[h[t][j]]=1;
			}
			else {//不互质则暴力找一个符合的数 
				while(1){
					++t;
					if(prime(t)){
						for(int j=1;j<=m;j++){
							if(!(t%pri[j]))vp[j]=1;
							if(pri[j]>t)break;
						}
						break;
					}
				}
			}
		}
		cnt++;
		b[i]=t;
	}
	int i=1;
	while(cnt!=n) {//从小找没用过的质数 
		while(vp[i])i++;
		vp[i]=1;
		b[++cnt]=pri[i];
	}
	for(int i=1;i<=n;i++)printf("%d ",b[i]);
	return 0;
}

