typedef long long ll;
const ll mod = 998244353;
ll Add(ll x, ll y) { return (x + y >= mod) ? (x + y - mod) : (x + y); }
ll Mul(ll x, ll y) { return x * y % mod; }
ll Mod(ll x) { return (x < 0) ? (x + mod) : (x >= mod ? (x - mod) : x); }
template <typename T>
T &read(T &r) {
	r = 0; bool w = 0; char ch = getchar();
