{
	cin>>n>>m;
	for(int i=1;i<=n;i++)
	{
		cin>>a[i];
		s[i]=s[i-1]+a[i];
	}
	long long building=1;
	long long ask=0;
	for(int i=1;i<=m;i++)
	{
		cin>>ask;
		while(ask>s[building])
