		}
		if(s=='U') c = ( (c>>m)&a) | (c&(b<<m));
		else if(s=='L') c = ((c>>1)&a) | (c&(b<<1));
		else if(s=='D') c = ((c<<m)&a) | (c&(b>>m));
		else if(s=='R') c = ((c<<1)&a) | (c&(b>>1));
	}
	if(c==e)
		printf("%d\n",k);
	else
		printf("%d\n",-1);



