	string s;
	int n,i;
	cin>>n;
	for(i=1;i<=n;i++)
	{
		a[i].id=i;
		cin>>a[i].w;
		q1.push(a[i]);
	}
	cin>>s;
	int m=2*n;
	for(i=0;i<m;i++)
	{
		if(s[i]=='0')
		{
			//如果是内向的，输出堆顶、删除堆顶，并装进适合外向的堆里
         		B t=q1.top();
			q1.pop();
			q2.push(t);
			cout<<t.id<<" ";
