#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
char s[25];
int n,a[15],sum[15];
ll fac[25],ans;
void dfs(int x,int tot){
	if(x==10){
		ll now=fac[tot];
		for(int i=0;i<=9;i++){
			now/=fac[a[i]];
		}
		if(a[0]>0){
			ll jian=fac[tot-1]/fac[a[0]-1];
			for(int i=1;i<=9;i++)	jian/=fac[a[i]];
			now-=jian;
		}
		ans+=now;
		return;
	}
	if(sum[x]==0){
		dfs(x+1,tot);
		return;
	}
	for(int i=1;i<=sum[x];i++){
		a[x]=i;
		dfs(x+1,tot+i);
	}
}
int main(){
	fac[0]=1;
	for(int i=1;i<=20;i++)	fac[i]=fac[i-1]*i;
	scanf("%s",s+1);
	n=strlen(s+1);
	for(int i=1;i<=n;i++)	sum[s[i]-'0']++;
	dfs(0,0);
	printf("%lld",ans);
	return 0;
}
