#include <cstdio>
#include <iostream>
#include <cmath>
using namespace std;
int n,num;
int main(){
	cin>>n;
	while(n!=0){
		if(n>=100){
			int h=floor(n/100);
			n-=(h*100);
			num+=h;
			continue;
		}
		if(n>=20){
			int b=floor(n/20);
			n-=(b*20);
			num+=b;
			continue;
		}
		if(n>=10){
			int d=floor(n/10);
			n-=(d*10);
			num+=d;
			continue;
		}
		if(n>=5){
			int t=floor(n/5);
			n-=(t*5);
			num+=t;
			continue;
		}
		if(n>=1){
			int p=floor(n/1);
			n-=p;
			num+=p;
			continue;
		} 
	}
	cout<<num;
}
```
