#include <bits/stdc++.h>
using namespace std;
bool Pr[1000005];
int main()
{
	int x,y,cnt = 0;
	cin >> x >> y;
	if (x > y) swap(x,y);
	memset(Pr,1,sizeof(Pr));
	Pr[1] = Pr[0] = 0;
	for (int i = 2;i <= y;i++)
		if (Pr[i])	
			for (int j = 2;j * i <= y;j++)//筛出合数，将其标记。
				Pr[i * j] = 0;		
	for (int i = x;i <= y;i++)
		if (Pr[i]) cnt++;
	cout << cnt;
	return 0;
 } 
