### 完整代码：

```cpp
#include<bits/stdc++.h>
using namespace std;

#define int long long
const int maxn = 100005;
int n, m;
int cnt, hd[maxn];
struct node{
	int to, nxt;
}e[maxn * 2]; 
int dfn[maxn], low[maxn];
int tmp, top;
int st[maxn];
int co[maxn], col;

void add (int u, int v)
{
	e[++cnt].to = v;
	e[cnt].nxt = hd[u];
	hd[u] = cnt;
}

void tarjan (int u)
{
	dfn[u] = low[u] = ++tmp;//step 1
	st[++top] = u;//step 2
	for (int i = hd[u]; i; i = e[i].nxt)//step 3
	{
		int v = e[i].to;
		if (!dfn[v])//如果没有被访问过
		{
			tarjan (v);
			low[u] = min (low[u], low[v]);
		}
		else if (!co[v]) //如果它还不在一个强连通分量内
        low[u] = min (low[u], dfn[v]);

	}
	if (low[u] == dfn[u])//step 4
	{
		co[u] = ++col;
		while (st[top] != u)//弹栈操作
		{
			co[st[top]] = col;
			--top;
		} 
		--top;//最后记得把 u 也弹出去
	}
}

int vis[maxn];

signed main ()
{
	scanf ("%lld %lld", &n, &m);
	for	(int i = 1; i <= m; i++)
	{
		int u, v;
		scanf ("%lld %lld", &u, &v);
		add (u, v);
	}
	for (int i = 1; i <= n; i++)
	{
		if (!dfn[i]) tarjan (i);
	}
	printf ("%lld\n", col);
	for (int i = 1; i <= n; i++)
	{
		if (vis[i]) continue;
		printf ("%lld ", i);
		vis[i] = 1;
		for (int j = i + 1; j <= n; j++)
		{
			if (co[j] == co[i])
			{
				printf ("%lld ", j);
				vis[j] = 1;
			}
		}
		printf ("\n");
	}
	return 0;
}
```




