```cpp
#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;
#define int long long
const int N = 50005, M = 300005;
int head[N], ver[M], Next[M];
int dfn[N], low[N], c[N], stack[N];
int n, m, tot = 1, num, root, top, cnt;
bool cut[N];
vector<int> dcc[N];//dcc[i] 存储编号为 i 的 v-DCC 中的所有节点

void add(int x, int y) {//邻接表存图
	ver[++tot] = y, Next[tot] = head[x], head[x] = tot;
}

void tarjan(int x) {
	dfn[x] = low[x] = ++num;
	stack[++top] = x;
	//if (x == root && head[x] == 0) {//孤立点，不过本题不需要
		//dcc[++cnt].push_back(x);
		//return;
	//}
	int flag = 0;
	for (int i = head[x]; i; i = Next[i]) {
		int y = ver[i];
		if (!dfn[y]) {
			tarjan(y);
			low[x] = min(low[x], low[y]);
			if (low[y] >= dfn[x]) {
				flag++;
				if (x != root || flag > 1)
					cut[x] = true;
				cnt++;
				int z;
				do {
					z = stack[top--];
					dcc[cnt].push_back(z);
				} while (z != y);
				dcc[cnt].push_back(x);
			}
		} else
			low[x] = min(low[x], dfn[y]);
	}
}

bool cmp(vector<int> x, vector<int> y){//字典序排序函数
  	for (register int i = 0; i < min(x.size(), y.size()); i++) {
    	if (x[i] < y[i]) 
			return true;
    	else if (x[i] > y[i]) 
			return false;
  	}
  	return x.size() < y.size();
}

signed main() {
	ios::sync_with_stdio(false);//关闭与scanf的同步来提速
	cin >> n >> m;
	for (register int i = 1; i <= m; i++) {
		int x, y;
		cin >> x >> y;
		if (x == y)
			continue;
		add(x, y), add(y, x);
	}
	for (register int i = 1; i <= n; i++)
		if (!dfn[i])
			root = i, tarjan(i);
	for (register int i = 1; i <= cnt; i++) 
		sort(dcc[i].begin(), dcc[i].end());//排序
	sort(dcc + 1, dcc + cnt + 1, cmp);
	cout << cnt << "\n";
	for (register int i = 1; i <= cnt; i++) {//输出
		for (register int j = 0; j < dcc[i].size(); j++)
			cout << dcc[i][j] << ' ';
		cout << "\n";
	}
	return 0;
}
