#include<cstdio>
#define reg register
inline int s()
{
    reg char ch=getchar();
    reg int re=0;
    reg bool fl=1;
    if(ch=='-')
    {
        fl=0;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9')
    {
        re=re*10+ch-'0';
        ch=getchar();
    }
    return fl?re:-re;
}
inline bool w(reg int r)
{
    if(r>9)
        w(r/10);
    putchar(r%10+'0');
    return 1;
}
int main()
{
    reg int a=s(),b=s();
    if(a+b>=0)
        return !w(a+b);
    putchar('-');
    return !w(-a-b);
}
