using namespace std;
int a[10][10]= {0},n,sum=0;
int f[10][10]= {0},hh[10][10]= {0};
void search(int m,int x,int y,int h,int s) {
    int ji;
    if(f[x][y]==h&&hh[x][y]==s) {
        return;
    }
    if(x>n||y>n) {
        return;
    }
    f[x][y]=h;
    hh[x][y]=s;
    if(x==n&&y==n&&h==1) {
        ji=a[x][y];
        a[x][y]=0;
        search(1,1,1,2,s+ji);
        a[x][y]=ji;
    } else if(x==n&&y==n&&h==2) {
        if(s>sum) {
            sum=s;
        }
        return;
    } else {
        if(a[x][y]!=0) {
            ji=a[x][y];
            a[x][y]=0;
            search(m+1,x+1,y,h,s+ji);
            search(m+1,x,y+1,h,s+ji);
            a[x][y]=ji;
        }
        search(m+1,x+1,y,h,s);
        search(m+1,x,y+1,h,s);
    }
}
