AC代码：
```cpp
#include<bits/stdc++.h>
using namespace std;
struct data
{
    int len;
    int s[1005];
};
data ans,anss;
int n;
data mul(data x,int y)
{
    data c;
    for(int i=1;i<=1000;i++)c.s[i]=0;
    for(int i=1;i<=x.len;i++)
    {
        c.s[i]+=x.s[i]*y;
        c.s[i+1]=c.s[i]/10;
        c.s[i]=c.s[i]%10;
    }
    int lan=x.len;
    while(c.s[lan+1]>0)
    {
        lan++;
        c.s[lan+1]+=c.s[lan]/10;
        c.s[lan]=c.s[lan]%10;
    }
    c.len=lan;
    return c;
}
data add(data x,data y)
{
    data c;
    for(int i=1;i<=1000;i++)c.s[i]=0;
    int lan=max(x.len,y.len);
    for(int i=1;i<=lan;i++)
    {
        c.s[i]+=x.s[i]+y.s[i];
        c.s[i+1]+=c.s[i]/10;
        c.s[i]=c.s[i]%10;
    }
    if(c.s[lan+1]>0)lan++;
    c.len=lan;
    return c;
}
int main()
{
    cin>>n;
    for(int i=1;i<=1000;i++)ans.s[i]=0;
    ans.len=0;
    for(int i=1;i<=n;i++)
    {
        for(int j=1;j<=1000;j++)anss.s[j]=0;
        anss.s[1]=1;
        anss.len=1;
        for(int j=1;j<=i;j++)anss=mul(anss,j);
        ans=add(ans,anss);
    }
    for(int i=ans.len;i>=1;i--)cout<<ans.s[i];
    return 0;
}
