		reverse(sa.begin(),sa.end());
		reverse(sb.begin(),sb.end());
		for(int j=0;j<la;j++){
			a[j]=sa[j]-'0';
		}
		for(int j=0;j<lb;j++){
			b[j]=sb[j]-'0';
		}
		for(int j=0;j<lc;j++){
			c[j]=a[j]+b[j]+c[j];
			if(c[j]>9){
				c[j+1]=c[j+1]+1;
				c[j]=c[j]%10;
			}
		}//基本高精度加法
		while(c[lc]>0){
			lc++;
		}//看有无最高位进位，其实if就可以了，懒得改
		memset(a,0,sizeof(a));
		sb="";
		for(int j=lc-1;j>=0;j--){
			sb=sb+char(c[j]+48);
		}
		memset(c,0,sizeof(c));
