void jiecheng(int x)
{
  int d,r;
  d=0;
  for(int i=1;i<=len;i++)
  {
    r=a[i];
    a[i]=(a[i]*x+d)%10000;
    d=(r*x+d)/10000;
  }
  if(d!=0)
    a[++len]=d;
}
void jiafa(int k)
{
  int d,r;
  d=0;
  for(int i=1;i<=k;i++)
  {
    r=b[i];
    b[i]=(b[i]+a[i]+d)%10000;
    d=(r+a[i]+d)/10000;
  }
  if(d!=0)
  {
    b[++k]=d;
    len1=k+1;
  }
  else
    len1=k;
}
