```cpp

#include <stdio.h>
#include <string.h>

char s[1000], t[1000], tmp[1000];

void BigAdd(char s[], char t[])
{
    char* p = s;
    char* q = t;

    int c=0;
    while ((*p)!='\0' && (*q)!='\0')
    {
        *p += *q+c-'0';
        c=0;
        if (*p > '9')
        {
            *p -= 10;
            if (*(p+1)=='\0') *(p+1)='0';
            if (*(q+1)=='\0') *(q+1)='0';
            c=1;
        }
        p++;
        q++;
    }

    while (*q!='\0' && *p=='\0') *p++ = *q++;
}

int main()
{
    memset(s, 0, sizeof(s)/sizeof(char));
    memset(t, 0, sizeof(t)/sizeof(char));
    s[0]='0';
    t[0]='1';

    int n;
    scanf("%d", &n);

    int i;
    for (i=1; i<=n; ++i)
    {
        strcpy(tmp, t);
        int j=i;
        while(--j) BigAdd(t, tmp);
        BigAdd(s, t);
    }

    for (i=strlen(s)-1; i>=0; --i) putchar(s[i]);
    printf("\n");
    return 0;
}

