					first = 0;
					cout<<"2";
				}
				else

				continue;
			}
			if(first)
			{
				first = 0;
				cout<<"2"<<"(";
				f(i);
				cout<<")";
			}
			else 
			{
				cout<<"+"<<"2"<<"(";    //如果不是第一次的话就要前面加一个+号
				f(i);
				cout<<")";
			}
		}
	}
