```cpp
#include<iostream>
#include<stack>
#include<string>
using namespace std;
string num[20009];   //用于存储已经计算过的幂次方值 
string solve(int now){
    if(num[now] != "")return num[now];   //如果在之前已经计算过当前值的幂次方则直接返回，减少计算量 
    string ans;               //答案 
    stack<int> sta;            // 存二进制的栈 
    int x = now;        //将当前数字now化成二进制用栈存储 
    while(x){
        sta.push(x%2);
        x /= 2;
    }
    int len = sta.size(),first = 1;   //first为输出变量，用于正确输出加号，Len为栈大小， 
    for(int i = len; i >= 1; i--){
        x = sta.top(); sta.pop();
        if(x != 0){
            if(first)first = 0;     
            else ans += '+';
            if(i == 1)ans += num[1];        //特判 
            else if(i == 2)ans += num[2];   //特判 
            else {
                ans += "2(";                //指数部分大于2则递归计算答案，并存入ans          
                ans += solve(i-1); 
                ans += ")";
            }
        }
    }
    num[now] = ans;                       //对当前now值进行记忆化，减少计算 
    return ans;
}
int main(){
    int n;
    cin >> n;
    num[1] = "2(0)";             //底层数据初始化 
    num[2] = "2";
    num[3] = "2+2(0)";
    num[4] = "2(2)";
    string ans = solve(n);
    cout << ans <<endl;
    return 0;
}
