long long a,n,x,t1,t2,s1,s2,tt,ss;
double t,m;
int main()
{
    cin>>a>>n>>m>>x;
    if (n==x) {cout<<0<<endl;return 0;}
    if (x<=2||n<=3) {cout<<a<<endl;return 0;}
    if (x==3) {cout<<a*2<<endl;return 0;}
    t1=1;t2=2;s1=s2=0;
    for (int i=4;i<=n-1;i++)
    {    
        tt=t1;t1=t2;
        t2=tt+t2-1;    
        ss=s1;s1=s2;
        s2=ss+s2+1;    
    }    
    t=(m-t2*a)/s2;
    if (trunc(t)<t) {cout<<"No answer.";return 0;}
    t1=1;t2=2;s1=s2=0;
    for (int i=4;i<=x;i++)
    {    
        tt=t1;t1=t2;
        t2=tt+t2-1;    
        ss=s1;s1=s2;
        s2=ss+s2+1;    
    }    
    cout<<t2*a+s2*t;
    return 0;
}
