#include<bits/stdc++.h>
using namespace std;
void jz(int m,int n)
{
	int tp,kp;//储存输出数据 
	if(m==0) return;
	else
	{
		tp=m%n;//这是求模 
		m=m/n;
		jz(m,n);//递归 
	}
	if(tp>=10)
	{
		kp=tp-10+65;
		printf("%c",kp);//你懂得 
	}
	else printf("%d",tp);
}
int main()//主程序 
{
	int ten,r;
	cin>>ten>>r;
	int qp=r;
	jz(ten,r);
	return 0;
}
