			cnt=0;memset(zh,0,sizeof zh);
			for(kk=i-1;kk<j;kk++) zh[cnt++]=fz[kk];
			reverse(zh,zh+strlen(zh));
			dp[i][j][0]=num(zh);
            //添加0个乘号即为它本身
		}
	for(l=1;l<=k;l++)
    //要添加的乘号数
		for(len=2;len<=n;len++)
        //按长度枚举，保证长度短的先被枚举
			for(i=1;i<=n-len+1;i++)
			{
            //i为左端点，j为右端点
				j=i+len-1;
				for(sl=0;sl<l;sl++)
				for(kk=i+1;kk<=j;kk++)
				{
						cc=dp[i][kk-1][sl]*dp[kk][j][l-sl-1];
						if(dp[i][j][l]<cc) dp[i][j][l]=cc;//就是上面的方程
				}
			}
	dp[1][n][k].print();
	return 0;
}  
