#include <cstdio>
#include <cstring>
using namespace std;
int f[45][45][8];    //记忆化搜索 
int vis[45][45][8];
int tem;
int a[45];
int dfs(int left, int right, int k){ //[left, right]
    if (vis[left][right][k]) return f[left][right][k];
    if (k == 0) {
        vis[left][right][k] = 1;
        tem = 0;
        for (int o = left; o <= right; o++)
            tem = tem * 10 + a[o];
        f[left][right][k] = tem;
        return tem;
    }
    else {
        for (int i = left; i < right; i++){    //断点
            for (int j = 0; j < k; j++){
                f[left][right][k] = max(f[left][right][k], dfs(left, i, j) * dfs(i + 1, right, k - j - 1));
            }
        }
        vis[left][right][k] = 1;
        return f[left][right][k];
    }
}
int main(){
    memset(vis, 0, sizeof(vis));
    memset(f, 0, sizeof(f));
    int n, k;
    cin >> n >> k;
    string b;
    cin >> b;
    for (int i = 0; i < n; i++)    
        a[i] = b[i] - '0';
    cout << dfs(0, n - 1, k);
    return 0;
}

