```cpp
var
  n,m,i,k,p,j:longint;
  d:array[1..50,1..10] of int64;
  s:string;
function make(p,q:longint):longint;//将s的第p个位置拷q个进行转化
var x,c:longint;
begin
  val(copy(s,p,q),x,c);
  make:=x;
end;
begin
  readln(n,m);
  readln(s);
  for i:=1 to n do d[i,1]:=make(1,i);//初始化
  for k:=2 to m+1 do//枚举乘号数
   for i:=k to n do
    for j:=i downto k do
     begin
       p:=make(j,i-j+1);
       if d[i,k]<d[j-1,k-1]*p then//选取最优值
          d[i,k]:=d[j-1,k-1]*p;
     end;
    writeln(d[n,m+1]);
end.
