```cpp
#include <iostream>
#include <cstdio>
#include <string>
#include <cmath>
using namespace std;
const int M=11;
string a;
long long  n,k,f[M][M];
bool init(){
    cin>>n>>k>>a;  
    k++;
    if((n<k)||(n==0)){
       cout<<0;
       return 1;
    }
    return 0;
}
long long conver(int i,int j){//转换为数字
    string t;
    long long l,sum=0;
    t=a.substr(i,j);
    for(l=0;l<t.length();l++){
       sum=sum*10+t[l]-48;
    }
    return sum;    
}
void slove(){
     long long  i,j,l,Max,t;
     for(i=1;i<=n;i++)f[i][1]=conver(0,i);
     for(j=2;j<=k;j++){
        for(i=j;i<=n;i++){
           t=0;
           for(l=1;l<i;l++){
               Max=f[l][j-1]*conver(l,i-l);
               if(t<Max)t=Max;
           }
           f[i][j]=t;
        }
     }
}
int main(){
    if(!init()){//预判断
       slove();
       cout<<f[n][k];
    }
    return 0;
}
