          if (c[i]=='+') fh=1; //同理
          if (c[i]=='=') {dh=-1;fh=1;}//如果在等号右边，所有的符号都会颠倒；fh重新变成+
         }
         else k=k*10+c[i]-48;//否则将此数存下来
    }
          zs+=k*fh*dh;//最后一次特殊处理，因为可能还有
}
