            t = 1;
        }
        else if(a[i]=='=')//等号右边的数据符号与左边的符号应该相反，故用k来改变其符号
        {
            while(!Q.empty())
            {
                s = s*10+Q.front();
                Q.pop();
            }
            s1+=t*k*s;
            t = 1;
            k = -1;
        }
        else
        {
            c = a[i];
            while(!Q.empty())
            {
                s = s*10+Q.front();
                Q.pop();
            }
            if(s==0)
                s = 1;//当未知数前面的系数为1时可以省略，此时s=0应改为1
            s2+=t*k*s;
        }
    }
    s = 0;//取出队列中的元素和s1相加
    while(!Q.empty())
    {
        s = s*10+Q.front();
        Q.pop();
    }
    s1+=t*k*s;
    s1  = -s1;
    if(s1==0)//防止输出为-0.000
        printf("%c=0.000\n",c);
    else
        printf("%c=%.3f\n",c,1.0*s1/s2);
    //cout<< setiosflags(ios::fixed) << setprecision(3) << 1.0*s1/s2 << endl;
    return 0;
}

