            if(equ[i]<'a'||equ[i]>'z')
                (flag?cons1:cons2)+=(pom?thisnum:-thisnum);
        }
        if(equ[i]=='+') pom=true;
        if(equ[i]=='-') pom=false;
        if(equ[i]>='a'&&equ[i]<='z')
        {
            p=equ[i];
