            if(equ[i-1]<'0'||equ[i-1]>'9')
                now=1;
            else now=thisnum;
            (flag?vari1:vari2)+=(pom?now:-now);
        }
        if(equ[i]=='=') {flag=false;pom=true;}
    }
    cons2+=pom?atoi(thisstr.c_str()):-atoi(thisstr.c_str());
    double ans;
